class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data.comment);$(`#post-comments-${e}`).append(o),t.deleteComment($(" .delete-comment-button",o)),new toggleLike($(" .toggle-like-button",o)),new Noty({theme:"relax",text:"Comment Added",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`<li id="comment-${e._id}" class="each-comment">\n                        <p class="each-comment-text">\n                            \n                            <small class="small-delete">\n                                <a class="delete-comment-button" href="/comments/destroy/${e._id}">X</a>\n                            </small>\n\n                        <div class="comment-user">\n                            <img src="${e.user.avatar}" alt="${e.user.name}" width="100">\n                            <p class="comment-user-name"> ${e.user.name} </p>\n                            <p class="comment-timing">comment timing</p>\n\n                            <br>\n                            <small>\n\n                                <a class="toggle-like-button" data-likes="0" href="/likes/toggle/?id=${e._id}&type=Comment">                        <i class="fa-solid fa-heart"></i>\n                                ${e.likes.length}\n\n                                </a>\n\n                            </small>\n                        </div>\n                        <p class="comment-content">${e.content}</p>\n                        </p>    \n                </li>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$(`#comment-${e.data.comment_id}`).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}