class ToggleLike{constructor(e){this.toggler=e,this.toggleLike()}toggleLike(){$(this.toggler).click((function(e){e.preventDefault();let t=this;$.ajax({type:"POST",url:$(t).attr("href")}).done((function(e){let l=parseInt($(t).attr("data-likes"));console.log(l),1==e.data.deleted?(l--,console.log("- like",l),$(t).html(`<i class="fa-regular fa-heart"></i> ${l}`)):(l++,console.log("+ like",l),$(t).html(`<i class="fa-solid fa-heart"></i> ${l}`)),$(t).attr("data-likes",l),$(t).html(`${l} Likes`)})).fail((function(e){console.log("error in completing the request")}))}))}}